import React from 'react';
import {Form} from 'react-bootstrap';


class loginForm extends React.Component {

   constructor(props) {
       super(props)
       this.state = {
           name: '',
           password: '',
           medicid: ''
       }

       this.handleChange = this.handleChange.bind(this);
       this.handleSubmit = this.handleSubmit.bind(this);
   }

   handleChange(e) {
       this.setState({
           value: e.target.value
       });
   }

   handleSubmit(e) {
       e.preventDefault();
   }

    render() {
        return (
            <div className="loginForm">
                <Form>
                  <Form.Row>
                    <label htmlFor="nameInput">
                      Name
                      <input 
                        id="nameInput"
                        name="name"
                        type="text" 
                        value={this.state.value}
                        onChange={this.handleChange}
                      />
                    </label>
                    <label htmlFor="medicIdInput">
                      MedicId
                      <input 
                        id="medicIdInput"
                        name="medicid"
                        type="number" 
                        value={this.state.value}
                        onChange={this.handleChange}
                      />
                    </label>
                    <label htmlFor="passwordIdInput">
                      Password
                      <input 
                        id="passwordIdInput"
                        name="password"
                        type="password" 
                        value={this.state.value}
                        onChange={this.handleChange}
                      />
                    </label>
                    <input
                       type="submit"
                       value="Submit"
                    />
                  </Form.Row>
                </Form>
            </div>
        )
    }
}

export default loginForm;
