import React from 'react';
import {Form,Row,Col,Container,Button} from 'react-bootstrap';


class loginForm extends React.Component {

   constructor(props) {
       super(props)
       this.state = {
           name: '',
           password: '',
           medicid: ''
       };

     //  this.handleChange = this.handleChange.bind(this);
      // this.handleSubmit = this.handleSubmit.bind(this);
   }

  handleChange =  (e) => {
    this.setState({   [e.target.name]: e.target.value  });

  }
  clearForm = () => {


  }

  handleSubmit = (e) => {
    e.preventDefault();
    this.clearForm();
    console.log(this.state)
  }

    render() {

        return (

            <Container className="center" >
                <Form onSubmit={e => this.handleSubmit(e)}>
                  <Form.Group as={Row}  >
                    <Form.Label column sm={2}>
                      Name
                    </Form.Label>

                    <Col sm={10} >
                        <Form.Control 
                          size="lg"
                          id="nameInput"
                          name="name"
                          type="text" 
                        //  value={this.state.value}
                          onChange={e => this.handleChange(e)}
                        />
                    </Col>
                    </Form.Group>
                    <Form.Group as={Row} >
                      <Form.Label column sm={2} >
                        MedicId
                      </Form.Label>
                      <Col sm={10}>
                          <Form.Control 
                            id="medicIdInput"
                            name="medicid"
                            type="text" 
                           // value={this.state.value}
                            onChange={e => this.handleChange(e)}
                          />
                      </Col>
                    </Form.Group>
                    <Form.Group as={Row} >
                      <Form.Label column sm={2}>
                        Password
                      </Form.Label>
                      <Col sm={10}>
                          <Form.Control
                            id="passwordIdInput"
                            name="password"
                            type="password" 
                           // value={this.state.value}
                            onChange={e => this.handleChange(e)}
                          />
                      </Col>
                    </Form.Group>
                    <Form.Group as={Row} >
                        <Col sm={{span: 6, offset: 6}}>
                          <Button type="submit" value="Submit" >
                            Submit
                          </Button>
                        </Col>
                    </Form.Group>
                </Form>
            </Container>
        )
    }
}

export default loginForm;
