import React from 'react';
import {Form,Row,Col,Container} from 'react-bootstrap';


class loginForm extends React.Component {

   constructor(props) {
       super(props)
       this.state = {
           name: '',
           password: '',
           medicid: ''
       }

       this.handleChange = this.handleChange.bind(this);
       this.handleSubmit = this.handleSubmit.bind(this);
   }

   handleChange(e) {
       this.setState({
           value: e.target.value
       });
   }

   handleSubmit(e) {
       e.preventDefault();
   }

    render() {
        return (
            <div className="loginForm" as={Container}>
                <Form>
                  <Form.Group as={Row} className="text-center" >
                    <Form.Label >
                      Name
                    </Form.Label>
                    <Col sm={10} >
                        <Form.Control 
                          size="lg"
                          id="nameInput"
                          name="name"
                          type="text" 
                          value={this.state.value}
                          onChange={this.handleChange}
                        />
                    </Col>
                    </Form.Group>
                    <Form.Group as={Row} >
                      <Form.Label >
                        MedicId
                      </Form.Label>
                      <Col>
                          <Form.Control 
                            id="medicIdInput"
                            name="medicid"
                            type="number" 
                            value={this.state.value}
                            onChange={this.handleChange}
                          />
                      </Col>
                    </Form.Group>
                    <Form.Group as={Row} >
                      <Form.Label >
                        Password
                      </Form.Label>
                      <Col>
                          <Form.Control
                            id="passwordIdInput"
                            name="password"
                            type="password" 
                            value={this.state.value}
                            onChange={this.handleChange}
                          />
                      </Col>
                    </Form.Group>
                    <Form.Group as={Row}>
                        <Form.Control
                          type="submit"
                          value="Submit"
                        />
                    </Form.Group>
                </Form>
            </div>
        )
    }
}

export default loginForm;
